import React from 'react'

import {  useState } from "react";

const DairyCards = () => {
    const [data, setData] = useState({
        title: "",
        content: ""
    });


    function onHandle(e) {
        e.preventDefault();
    }
return (
<>
<h1>Welcome to your Dairy Session</h1>

<form onSubmit={onHandle} action='' method=''>
    <input type="text" name = "title" value = {data.title} placeholder='how are you feeling' size="39" onChange={e => setData(e.target.value)} required ></input>
    <br />
    <br/>
    <textarea name= "content" value={data.content} placeholder='what happened' rows="8" cols="41" onChange={e => setData(e.target.value)} required></textarea>
    <br />
    <br/>
    <button>Share your thoughts</button>
</form>

</>
);
}
// };


export default DairyCards
