return (
<>
<h1>Welcome to your Dairy Session</h1>

<form onSubmit='' action='' method=''>
    <input type="text" name = "title" value = {title} placeholder='how are you feeling' size="39" onChange={e => setTitle(e.target.value)} required ></input>
    <br />
    <br/>
    <textarea name= "content" value={content} placeholder='what happened' rows="8" cols="41" onChange={e => setContent(e.target.value)} required></textarea>
    <br />
    <br/>
    <button>Share your thoughts</button>
</form>

</>
);
}
