import React from 'react'
import { useState } from 'react'
import DairyCards from './DairyCards'
import { useNavigate } from "react-router-dom";
const Login = () => {
    // const [message, setMessage] = useState("")
    const [password, setPassword] = useState();
    const [checkName, setCheckName] = useState("")
    const [authenticated, setAuthenticated] = useState(localStorage.getItem(localStorage.getItem("Authenticated")) || false)
    const navigate = useNavigate()

 const users = [{username: checkName, password: "fatoki"}];

function handleSubmit(event){
  const account = users.find((user)=> user.username === checkName)
  if( account && account.password === password){
    setAuthenticated(true)
    localStorage.setItem("authenticated", true)
  }
    event.preventDefault();
    navigate("/home")
}
  return (
    <form onSubmit={handleSubmit}>


        <input type="text" placeholder="Username" value={checkName} onChange={e => setCheckName(e.target.value)}></input>
        <input type="password" placeholder="Password" onChange={e => setPassword(e.target.value)}></input>

    <button type='submit'> Enter</button>

    </form>

  )
}

export default Login
